import { useState, useEffect } from "react";
import Link from "next/link";
import PageLayout from "../../components/PageLayout";
import fetch from "isomorphic-unfetch";
import { Show } from "../../types/show";

const ShowItem = ({show}:Show) => (
  <article>
    {show.image && (
      <img src={show.image.medium} alt={show.name} />
    )}
    <h3>{show.name}</h3>
    <div>{show.summary}</div>
  </article>
)

const ShowSearchPage = () => {
  const [didSearch, setDidSearch] = useState(false);
  const [results, setResults] = useState<Show[]>([]);
  const [isLoading, setLoading] = useState(false);
  const [query, setQuery] = useState("");

  const search = async (e:React.FormEvent) => {
    e.preventDefault();

    if (isLoading || !query) {
      return;
    }

    setResults([]);
    setLoading(true);
    setDidSearch(false);

    const res = await fetch(`https://api.tvmaze.com/search/shows?q=${encodeURIComponent(query)}`);
    const data = await res.json();


    setLoading(false);
    setResults(data);
    setDidSearch(true);
  }

  useEffect(() => {
    if (didSearch) {
      setDidSearch(false);
    }
  }, [query]);

  return (
    <PageLayout>
      <h1>Search for movies</h1>
      <form onSubmit={search} className="mb-4">
        <fieldset disabled={isLoading}>
          <input type="search" className="form-control rounded-pill" value={query} onChange={e => setQuery(e.target.value)} required />
        </fieldset>
      </form>
      {results.length === 0 && didSearch && (
        <p className="mt-3 text-danger">No results :(</p>
      )}
      {results && (
        <ul className="list-unstyled">
          {results.map(movie => (
            <li key={movie.show.id}>
              <Link href="/shows/[id]" as={`/shows/${movie.show.id}`}>
                <a>
                  <ShowItem {...movie} />
                </a>
              </Link>
            </li>
          ))}
        </ul>
      )}
    </PageLayout>
  )
}

export default ShowSearchPage;
