import Link from "next/link";
import PageLayout from "../../components/PageLayout";
import fetch from "isomorphic-unfetch";

import { ShowData } from "../../types/show";

type Props = {
  show? : ShowData;
  showId: string,
}

const SingleShowPage = ({show, showId}:Props) => {
  if (!show) {
    return (
      <PageLayout>
        <h1>Nothing found for ID "{showId}"</h1>
        <Link href="/shows"><a>&larr; back to search</a></Link>
      </PageLayout>
    )
  }

  return (
    <PageLayout>
      <h1>{show.name}</h1>
      {show.image && (
        <img src={show.image.medium} alt={show.name}/>
      )}
      <Link href="/shows">
        <a>&larr; back to search</a>
      </Link>
    </PageLayout>
  );
};

SingleShowPage.getInitialProps = async (req):Promise<Props> => {
  console.log("Req: ", req);
  const res = await fetch(`https://api.tvmaze.com/shows/401`);
  const data = await res.json();

  return {
    show: data,
    showId: "Test"
  }
}

export default SingleShowPage;
