import { useState, useEffect } from "react";
import Link from "next/link";
import PageLayout from "../../components/PageLayout";
import fetch from "isomorphic-unfetch";
import { Movie } from "../../types/movie";

const MovieItem = ({show}:Movie) => (
  <article>
    <img src={show.image.medium} alt={show.name} />
    <h3>{show.name}</h3>
    <div>{show.summary}</div>
  </article>
)

const ShowSearchPage = () => {
  const [didSearch, setDidSearch] = useState(false);
  const [results, setResults] = useState<Movie[]>([]);
  const [isLoading, setLoading] = useState(false);
  const [query, setQuery] = useState("");

  const search = async (e:React.FormEvent) => {
    e.preventDefault();

    if (isLoading || !query) {
      return;
    }

    setResults([]);
    setLoading(true);
    setDidSearch(false);

    const res = await fetch(`https://api.tvmaze.com/search/shows?q=${encodeURIComponent(query)}`);
    const data = await res.json();


    setLoading(false);
    setResults(data);
    setDidSearch(true);
  }

  useEffect(() => {
    if (didSearch) {
      setDidSearch(false);
    }
  }, [query]);

  return (
    <PageLayout>
      <h1>Search for movies</h1>
      <form onSubmit={search}>
        <fieldset disabled={isLoading}>
          <input type="search" className="form-control" value={query} onChange={e => setQuery(e.target.value)} required />
        </fieldset>
      </form>
      {results.length === 0 && didSearch && (
        <p className="mt-3 text-danger">No results :(</p>
      )}
      {results && (
        <ul className="list-unstyled">
          {results.map(movie => (
            <li key={movie.show.id}>
              <Link href="/movie/[id]" as={`/movie/${movie.show.id}`}>
                <a>
                  <MovieItem {...movie} />
                </a>
              </Link>
            </li>
          ))}
        </ul>
      )}
    </PageLayout>
  )
}

export default ShowSearchPage;
