import Link from "next/link";
import PageLayout from "../../components/PageLayout";
import fetch from "isomorphic-unfetch";

import { Show } from "../../types/show";

type Props = {
  show? : Show;
  showId: string,
}

const SingleShowPage = (props:Props) => {
  if (!props.show) {
    return (
      <PageLayout>
        <h1>Nothing found for ID "{props.showId}"</h1>
        <Link href="/shows"><a>&larr; back to search</a></Link>
      </PageLayout>
    )
  }

  const show = props.show.show;

  return (
    <PageLayout>
      <h1>{show.name}</h1>
      <Link href="/shows">
        <a>&larr; back to search</a>
      </Link>
    </PageLayout>
  );
};

SingleShowPage.getInitialProps = async ({req}):Promise<Props> => {
  console.log("Req: ", req);
  const res = await fetch(`https://api.tvmaze.com/shows/401`);
  const data = await res.json();

  return {
    show: data,
    showId: "Test"
  }
}

export default SingleShowPage;
